import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome } from '@fortawesome/free-solid-svg-icons';

// Sample attendance data
const initialAttendanceData = [
  { sno: 1, rollNo: '001', studentName: 'John Doe', class: '9', section: 'A', present: 'Yes', absent: 'No', leave: 'No', month: 1 },
  { sno: 2, rollNo: '002', studentName: 'Jane Smith', class: '9', section: 'A', present: 'No', absent: 'Yes', leave: 'No', month: 2 },
  { sno: 3, rollNo: '003', studentName: 'Michael Johnson', class: '9', section: 'A', present: 'No', absent: 'No', leave: 'Yes', month: 3 }
];

// MatrixTable component to display attendance data in a table
const MatrixTable = ({ data }) => {
  return (
    <div style={{ width: '80%', marginLeft: 'auto', marginTop: '20px' }}>
      <table border="1" style={{ width: '100%' }}>
        {/* Table header */}
        <thead style={{ backgroundColor: '#5DADE2' }}>
          <tr>
            <th>S.No</th>
            <th>Roll No</th>
            <th>Student Name</th>
            <th>Class</th>
            <th>Section</th>
            <th>Present</th>
            <th>Absent</th>
            <th>Leave</th>
          </tr>
        </thead>
        {/* Table body */}
        <tbody>
          {/* Mapping through attendance data to display rows */}
          {data.map((record, idx) => (
            <tr key={idx}>
              <td>{record.sno}</td>
              <td>{record.rollNo}</td>
              <td>{record.studentName}</td>
              <td>{record.class}</td>
              <td>{record.section}</td>
              <td>{record.present}</td>
              <td>{record.absent}</td>
              <td>{record.leave}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

// Navbar component
const Navbar = () => {
  // State variables
  const [selectedClass, setSelectedClass] = useState(''); // Selected class
  const [selectedSection, setSelectedSection] = useState(''); // Selected section
  const [selectedMonth, setSelectedMonth] = useState(''); // Selected month
  const [filteredData, setFilteredData] = useState(initialAttendanceData); // Filtered data state

  // Event handlers for dropdown and input changes
  const handleClassChange = (event) => {
    setSelectedClass(event.target.value); // Update selected class state
    filterData(event.target.value, selectedSection, selectedMonth); // Filter data based on class change
  };

  const handleSectionChange = (event) => {
    setSelectedSection(event.target.value); // Update selected section state
    filterData(selectedClass, event.target.value, selectedMonth); // Filter data based on section change
  };

  const handleMonthChange = (event) => {
    setSelectedMonth(event.target.value); // Update selected month state
    filterData(selectedClass, selectedSection, event.target.value); // Filter data based on month change
  };

  const filterData = (classValue, sectionValue, monthValue) => {
    let filtered = initialAttendanceData;
    if (classValue) {
      filtered = filtered.filter(record => record.class === classValue);
    }
    if (sectionValue) {
      filtered = filtered.filter(record => record.section === sectionValue);
    }
    if (monthValue) {
      filtered = filtered.filter(record => record.month === parseInt(monthValue));
    }
    setFilteredData(filtered);
  };

  return (
    <div className='containerStyle' style={{ alignItems: 'center', backgroundColor: 'white' }}>
      {/* Navbar header */}
      <div style={{ color: 'black', textAlign: 'left', backgroundColor: '#5DADE2', height: '10vh' }}>
        <h1>Staff</h1> {/* Main heading */}
        <a className="nav-link" href="" style={{ position: 'absolute', left: 0, top: 0 }}>
          <FontAwesomeIcon icon={faHome} style={{ background: 'none', height: '26px', marginTop: '35px', marginLeft: '1450px' }} />
        </a>
      </div>
      {/* Dropdown for Attendance Type */}
      <div style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
        {/* Attendance header */}
        <h2 style={{ fontSize: '10px', marginRight: '10px' }}>Attendance</h2>
        <select
          value={selectedClass}
          onChange={handleClassChange}
          style={{ width: '100px', marginRight: '10px' }}
        >
          <option value="">Select Class</option>
          <option value="9">9</option>
          <option value="10">10</option>
          {/* Add other class options */}
        </select>
        {/* Dropdown for Section */}
        <select
          value={selectedSection}
          onChange={handleSectionChange}
          style={{ width: '100px', marginRight: '10px' }}
        >
          <option value="">Select Section</option>
          <option value="A">A</option>
          <option value="B">B</option>
          {/* Add other section options */}
        </select>
        {/* Dropdown for Month */}
        <select
          value={selectedMonth}
          onChange={handleMonthChange}
          style={{ width: '100px', marginRight: '10px' }}
        >
          <option value="">Select Month</option>
          <option value="1">January</option>
          <option value="2">February</option>
          <option value="3">March</option>
          <option value="4">April</option>
          <option value="5">May</option>
          <option value="6">June</option>
          <option value="7">July</option>
          <option value="8">August</option>
          <option value="9">September</option>
          <option value="10">October</option>
          <option value="11">November</option>
          <option value="12">December</option>
          {/* Add options for other months */}
        </select>
        {/* View Attendance button */}
        <button
          className="view-attendance-button"
          style={{ background: 'blue', color: 'white' }}
          onClick={() => filterData(selectedClass, selectedSection, selectedMonth)}
        >
          View Attendance
        </button>
      </div>
      {/* Matrix table */}
      <MatrixTable data={filteredData} /> {/* Rendering the MatrixTable component with filtered data */}
    </div>
  );
};

export default Navbar;
