// export default Header;
import React, { useState } from 'react';
import './Header.css'; // Import the CSS file for styling

const Header = ({ title }) => {
  const [selectedMedium, setSelectedMedium] = useState('');
  const [selectedSession, setSelectedSession] = useState('');
  const [selectedClass, setSelectedClass] = useState(''); // State for selected class
  const classList = [1, 2]; // Example class list, replace with your actual data

  const handleMediumChange = (e) => {
    setSelectedMedium(e.target.value);
  };

  const handleSessionChange = (e) => {
    setSelectedSession(e.target.value);
  };

  const handleClassChange = (e) => {
    setSelectedClass(e.target.value);
  };

  return (
    <div>
      <div className="header-container">
        <h1 className="student-attendance-title">Student Attendance</h1>
        <div className="medium-session-container">
          <div className="medium-session-title">
            <label htmlFor="mediumDropdown" className="header-label">Medium:</label>
            <select
              id="mediumDropdown"
              value={selectedMedium}
              onChange={handleMediumChange}
              className="dropdown-select"
            >
              <option value="">Select Medium</option>
              <option value="hindi">Hindi</option>
              <option value="english">English</option>
            </select>
          </div>
          <div className="medium-session-title">
            <label htmlFor="sessionDropdown" className="header-label">Session:</label>
            <select
              id="sessionDropdown"
              value={selectedSession}
              onChange={handleSessionChange}
              className="dropdown-select"
            >
              <option value="">Select Session</option>
              <option value="2012-13">2012-13</option>
              <option value="2013-14">2013-14</option>
            </select>
          </div>
          <div className="class-title">
            <label htmlFor="classDropdown" className="header-label">Class:</label>
            <select
              id="classDropdown"
              value={selectedClass}
              onChange={handleClassChange}
              className="dropdown-select"
            >
              <option value="">Select class</option>
              {classList.map((classNumber, index) => (
                <option key={index} value={classNumber}>{classNumber}</option>
              ))}
            </select>
          </div>
        </div>
        <div className="student-table-container">
          <table className="student-table">
            <thead>
              <tr>
                <th>Student ID</th>
                <th>Student Name</th>
                <th>Father's Name</th>
                <th>Class</th>
                <th>Contact No</th>
                <th>Registration Date</th>
               
                <th>Make Admission</th>
                <th>Print</th>
                <th>Delete</th>
              </tr>
            </thead>
            <tbody>
              {/* Table rows will be added dynamically */}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Header;
