// export default Attendance;
import React, { useState } from 'react';
import './Attendance.css'; // Import the CSS file for styling

const Attendance = () => {
  const [selectedMedium, setSelectedMedium] = useState('Hindi');
  const [selectedSession, setSelectedSession] = useState('2013-14');
  const [roleDetails, setRoleDetails] = useState(null);
  const [selectedRole, setSelectedRole] = useState(null);
  const [selectedClass, setSelectedClass] = useState('1');
  const [selectedSection, setSelectedSection] = useState(null);
  const [selectedDate, setSelectedDate] = useState(null);
  const [attendanceData, setAttendanceData] = useState([]);
  const [existingData, setExistingData] = useState([
    { id: 1, name: 'John Doe', class: '9', section: 'A', rollNo: '001' },
    { id: 2, name: 'Jane Smith', class: '9', section: 'A', rollNo: '002' },
    { id: 3, name: 'Michael Johnson', class: '9', section: 'A', rollNo: '003' },
  ]);

  const handleMediumChange = (e) => setSelectedMedium(e.target.value);
  const handleSessionChange = (e) => setSelectedSession(e.target.value);
  const handleRoleClick = (role) => {
    if (role === 'Admin') {
      setRoleDetails({ name: 'Admin', description: 'Admin details here' });
      setSelectedRole('Admin');
    } else if (role === 'Teacher') {
      setRoleDetails({ name: 'Teacher', description: 'Teacher details here' });
      setSelectedRole('Teacher');
    } else if (role === 'Student') {
      setRoleDetails({ name: 'Student', description: 'Student details here' });
      setSelectedRole('Student');
    }
    setRoleDetails(null);
    setSelectedRole(role);
  };
  const handleSectionClick = (section) => setSelectedSection(section);
  const handleDateChange = (e) => setSelectedDate(e.target.value);

  const handleFetchAttendance = () => {
    // Here, you can fetch attendance data based on selectedClass, selectedSection, and selectedDate
    // For demonstration purposes, I'll set some dummy data
    const dummyData = [
      { id: 4, name: 'Emily Watson', class: '9', section: 'B', rollNo: '004', present: 20, absent: 2, leave: 3 },
      { id: 5, name: 'Daniel Brown', class: '9', section: 'B', rollNo: '005', present: 21, absent: 1, leave: 1 },
    ];

    // Merge existing data with fetched attendance data based on unique identifiers (e.g., id, rollNo)
    const mergedData = [...existingData];
    dummyData.forEach((newData) => {
      const existingIndex = mergedData.findIndex((data) => data.rollNo === newData.rollNo);
      if (existingIndex !== -1) {
        mergedData[existingIndex] = { ...mergedData[existingIndex], ...newData };
      } else {
        mergedData.push(newData);
      }
    });
    setExistingData(mergedData);
    setAttendanceData(dummyData);
  };

  return (
    <div className="attendance-container">
      <div className="medium-session-container">
        <div className="medium-session-title">
          <label htmlFor="mediumDropdown" className="header-label">Medium:</label>
          <select
            id="mediumDropdown"
            value={selectedMedium}
            onChange={handleMediumChange}
            className="dropdown-select"
          >
            <option value="Hindi">Hindi</option>
            <option value="English">English</option>
          </select>
        </div>
        <div className="medium-session-title">
          <label htmlFor="sessionDropdown" className="header-label">Session:</label>
          <select
            id="sessionDropdown"
            value={selectedSession}
            onChange={handleSessionChange}
            className="dropdown-select"
          >
            <option value="2012-13">2012-13</option>
            <option value="2013-14">2013-14</option>
          </select>
        </div>
      </div>
      <div className="attendance-header">
        <h1 className="attendance-title">Attendance Management</h1>
      </div>
      <div className="fill-attendance-container">
        <div className="fill-attendance-title">
          {roleDetails && (
            <div className="role-details">
              <h2>{roleDetails.name} Details</h2>
              <p>{roleDetails.description}</p>
            </div>
          )}
          <h3>Fill Attendance</h3>
          <div className="dropdown-container">
            <div className="class-dropdown" style={{ marginLeft: '20px' }}>
              <label htmlFor="classDropdown" className="header-label">Class:</label>
              <select
                id="classDropdown"
                value={selectedClass}
                onChange={(e) => setSelectedClass(e.target.value)}
                className="dropdown-select"
                style={{ width: '150px' }}
              >
                {[...Array(12).keys()].map((index) => (
                  <option key={index + 1} value={(index + 1).toString()}>
                    {(index + 1).toString()}
                  </option>
                ))}
              </select>
            </div>
            <div className="section-dropdown" style={{ marginLeft: '20px' }}>
              <label htmlFor="sectionDropdown" className="header-label">Section:</label>
              <select
                id="sectionDropdown"
                value={selectedSection}
                onChange={(e) => setSelectedSection(e.target.value)}
                className="dropdown-select"
                style={{ width: '150px' }}
              >
                {[...Array(4).keys()].map((index) => (
                  <option key={String.fromCharCode(65 + index)} value={String.fromCharCode(65 + index)}>
                    {String.fromCharCode(65 + index)}
                  </option>
                ))}
              </select>
            </div>
            <div className="date-container" style={{ marginLeft: '20px' }}>
              <label htmlFor="dateInput" className="header-label">Date:</label>
              <input
                type="date"
                id="dateInput"
                value={selectedDate}
                onChange={handleDateChange}
                className="date-input"
                style={{ width: '150px', height: '20px' }}
              />
            </div>
            <button className="fetch-button" onClick={handleFetchAttendance}>Fetch Attendance</button>
          </div>
        </div>
      </div>
      <div className="table-container">
        <h3>Existing Information</h3>
        <table className="attendance-table">
          <thead>
            <tr>
              <th>S.No</th>
              <th>Roll No.</th>
              <th>Student Name</th>
              <th>Class</th>
              <th>Section</th>
              <th>Month</th>
              <th>Present</th>
              <th>Absent</th>
              <th>Leave</th>
            </tr>
          </thead>
          <tbody>
            {existingData.map((data, index) => (
              <tr key={data.id}>
                <td>{index + 1}</td>
                <td>{data.rollNo}</td>
                <td>{data.name}</td>
                <td>{data.class}</td>
                <td>{data.section}</td>
                <td>April</td> {/* You can replace this with dynamic month */}
                <td>{data.present}</td>
                <td>{data.absent}</td>
                <td>{data.leave}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <div className="button-container">
        <button className="custom-button">Custom Button</button>
      </div>
    </div>
  );
};

export default Attendance;
