import React, { useState } from 'react';
import './Header.css'; // Import the CSS file for styling

const Header = ({ title }) => {
  const [inputValue, setInputValue] = useState('');
  const [suggestions, setSuggestions] = useState([]);

  const handleInputChange = (e) => {
    const value = e.target.value;
    setInputValue(value);

    // Example: Filter suggestions based on input value (replace with your logic)
    const filteredSuggestions = ['Apple', 'Banana', 'Cherry'].filter(suggestion =>
      suggestion.toLowerCase().includes(value.toLowerCase())
    );
    setSuggestions(filteredSuggestions);
  };

  return (
    <div className="header-container header-background">
      <h1 className="header-title">{title}</h1>
      <input
        type="text"
        placeholder="Enter text..."
        className="header-textbox"
        value={inputValue}
        onChange={handleInputChange}
      />
      <ul className="suggestions-list">
        {suggestions.map((suggestion, index) => (
          <li key={index}>{suggestion}</li>
        ))}
      </ul>
    </div>
  );
};

export default Header;
