import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome } from '@fortawesome/free-solid-svg-icons';

// MatrixTable component to display attendance data in a table
const MatrixTable = () => {
  // Sample attendance data
  const attendanceData = [
    { sno: 1, rollNo: '001', studentName: 'John Doe', class: '9', section: 'A', month: 'March', present: 'Yes', absent: 'No', leave: 'No' }
  ];

  return (
    <div style={{ width: '10%', marginLeft: 'auto' }}>
      <table border="1" style={{ width: '100%' }}>
        {/* Table header */}
        <thead>
          <tr>
            <th>S.No</th>
            <th>Roll No</th>
            <th>Student Name</th>
            <th>Class</th>
            <th>Section</th>
            <th>Month</th>
            <th>Present</th>
            <th>Absent</th>
            <th>Leave</th>
          </tr>
        </thead>
        {/* Table body */}
        <tbody>
          {/* Mapping through attendance data to display rows */}
          {attendanceData.map((record, idx) => (
            <tr key={idx}>
              <td>{record.sno}</td>
              <td>{record.rollNo}</td>
              <td>{record.studentName}</td>
              <td>{record.class}</td>
              <td>{record.section}</td>
              <td>{record.month}</td>
              <td>{record.present}</td>
              <td>{record.absent}</td>
              <td>{record.leave}</td>
