// export default Navbar;
import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome } from '@fortawesome/free-solid-svg-icons';

// MatrixTable component definition
const MatrixTable = () => {
  // Sample data representing class names and corresponding payment particulars
  const classData = [
    {
      className: 'Class 1',
      paymentParticulars: [
        { name: 'No of Payments', amount: 2500 },
        { name: 'Payment from Date', amount: 6000 },
        { name: 'Payment To Date', amount: 1200 },
        { name: 'Late Fee % Per Day', amount: 1200 },
        { name: 'Book Fee', amount: 1200 },
        { name: 'Management Fee', amount: 1200 },
        { name: 'Security Deposit', amount: 1200 },
        { name: 'Stationary Fee', amount: 1200 },
        { name: 'Transportation Fee', amount: 1200 },
        { name: 'Taxes', amount: 1200 },
        { name: 'Total', amount: 1200 }
      ]
    }
  ];

  // Logic to render the table using the classData array

  return (
    <div style={{ width: '50%', marginLeft: 'auto' }}>
      <table border="1" style={{ width: '100%' }}>
        <thead>
          <tr>
            <th>Class Name</th>
            {classData[0].paymentParticulars.map((particular, index) => (
              <th key={index}>{particular.name}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {classData.map((classItem, idx) => (
            <tr key={idx}>
              <td>{classItem.className}</td>
              {classItem.paymentParticulars.map((particular, index) => (
                <td key={index}>{particular.amount}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const Navbar = () => {
  // State variables for the dropdown selections
  const [selectedClass, setSelectedClass] = useState('');
  const [selectedSection, setSelectedSection] = useState('');
  const [selectedDate, setSelectedDate] = useState('');
  const [searchTerm, setSearchTerm] = useState('');

  // Functions to handle changes in the dropdown selections
  const handleClassChange = (event) => {
    setSelectedClass(event.target.value);
  };

  const handleSectionChange = (event) => {
    setSelectedSection(event.target.value);
  };

  const handleDateChange = (event) => {
    setSelectedDate(event.target.value);
  };

  // Function to handle changes in the search term input
  const handleSearchTermChange = (event) => {
    setSearchTerm(event.target.value);
  };

  return (
    <div className='containerStyle' style={{ alignItems: 'center', backgroundColor: 'white' }}>
      <div style={{ color: 'black', textAlign: 'left', backgroundColor: '#5DADE2 ', height: '10vh' }}>
        <h1>Account Management</h1>
        <a className="nav-link" href="" style={{ position: 'absolute', left: 0, top: 0 }}>
          <FontAwesomeIcon icon={faHome} style={{ background: 'none', height: '26px', marginTop: '35px', marginLeft: '1450px' }} />
        </a>
      </div>
      <div style={{ display: 'flex', gap: '80px' }}>
        <h2>Home</h2>
        <h2>Account Management</h2>
      </div>
      <hr style={{ width: '100%', height: '2px', backgroundColor: 'black', margin: '10px 0' }} />

      {/* Search box */}
      <div style={{ display: 'flex', justifyContent: 'flex-end', alignItems: 'center', marginBottom: '20px' }}>
        <label htmlFor="search" style={{ marginRight: '10px' }}>Search:</label>
        <input
          type="text"
          id="search"
          value={searchTerm}
          onChange={handleSearchTermChange}
          placeholder="Search..."
        />
      </div>

      {/* Class dropdown */}
      <div className="dropdown-container">
        <label htmlFor="classDropdown" className="dropdown-label">Class:</label>
        <select
          id="classDropdown"
          value={selectedClass}
          onChange={handleClassChange}
          className="dropdown-select"
        >
          <option value="">Select Class</option>
          <option value="1">1</option>
          <option value="2">2</option>
          {/* Add more class options as needed */}
        </select>
      </div>

      {/* Section dropdown */}
      <div className="dropdown-container">
        <label htmlFor="sectionDropdown" className="dropdown-label">Section:</label>
        <select
          id="sectionDropdown"
          value={selectedSection}
          onChange={handleSectionChange}
          className="dropdown-select"
        >
          <option value="">Select Section</option>
          <option value="A">A</option>
          <option value="B">B</option>
          {/* Add more section options as needed */}
        </select>
      </div>

      {/* Date picker */}
      <div className="dropdown-container">
        <label htmlFor="datePicker" className="dropdown-label">Date:</label>
        <input
          type="date"
          id="datePicker"
          value={selectedDate}
          onChange={handleDateChange}
          className="date-input"
          style={{ width: '142px' }} // Adjust the width as needed to match the dropdowns
        />
      </div>

      {/* Render the MatrixTable component */}
      <MatrixTable />
    </div>
  );
};

export default Navbar;
