import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome } from '@fortawesome/free-solid-svg-icons';

// MatrixTable component to display attendance data in a table
const MatrixTable = ({ attendanceData, searchTerm }) => {
  // Filtered attendance data based on search term
  const filteredData = attendanceData.filter((record) =>
    Object.values(record).some((value) =>
      value.toString().toLowerCase().includes(searchTerm.toLowerCase())
    )
  );

  // Attendance options for the dropdown
  const attendanceOptions = ['P', 'A', 'L', 'P/2'];

  // State variable for attendance dropdown value
  const [attendanceValue, setAttendanceValue] = useState('');

  // Event handler for attendance dropdown change
  const handleAttendanceChange = (event, idx) => {
    const { value } = event.target;
    const updatedData = [...filteredData];
    updatedData[idx].attendance = value;
    setAttendanceValue(value);
  };

  return (
    <div style={{ width: '100%', marginLeft: 'auto' }}>
      <table border="1" style={{ width: '100%' }}>
        {/* Table header with custom background color */}
        <thead style={{ backgroundColor: '#5DADE2', color: 'black' }}>
          <tr>
            <th>S.No</th> {/* Serial number */}
            <th>Roll No</th> {/* Roll number */}
            <th>Student Name</th> {/* Student name */}
            <th>Class</th> {/* Class */}
            <th>Section</th> {/* Section */}
            <th>Attendance</th> {/* Attendance status */}
          </tr>
        </thead>
        {/* Table body */}
        <tbody>
          {/* Mapping through filtered attendance data to display rows */}
          {filteredData.map((record, idx) => (
            <tr key={idx}>
              <td>{record.sno}</td> {/* Displaying serial number */}
              <td>{record.rollNo}</td> {/* Displaying roll number */}
              <td>{record.studentName}</td> {/* Displaying student name */}
              <td>{record.class}</td> {/* Displaying class */}
              <td>{record.section}</td> {/* Displaying section */}
              {/* Attendance dropdown */}
              <td>
                <select value={record.attendance} onChange={(event) => handleAttendanceChange(event, idx)}>
                  {attendanceOptions.map((option, index) => (
                    <option key={index} value={option}>
                      {option}
                    </option>
                  ))}
                </select>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

// Navbar component
const Navbar = () => {
  // Sample attendance data
  const [attendanceData] = useState([
    { sno: 1, rollNo: '001', studentName: 'John Doe', class: '9', section: 'A', attendance: 'P' },
    { sno: 2, rollNo: '002', studentName: 'Jane Smith', class: '9', section: 'A', attendance: 'A' },
    { sno: 3, rollNo: '003', studentName: 'Michael Johnson', class: '9', section: 'A', attendance: 'P/2' }
  ]);

  // State variables
  const [selectedClass, setSelectedClass] = useState(''); // Selected class
  const [selectedSection, setSelectedSection] = useState(''); // Selected section
  const [selectedDate, setSelectedDate] = useState(''); // Selected date
  const [searchTerm, setSearchTerm] = useState(''); // Search term

  // Event handlers for dropdown and input changes
  const handleClassChange = (event) => {
    setSelectedClass(event.target.value); // Update selected class state
  };

  const handleSectionChange = (event) => {
    setSelectedSection(event.target.value); // Update selected section state
  };

  const handleDateChange = (event) => {
    setSelectedDate(event.target.value); // Update selected date state
  };

  const handleSearchTermChange = (event) => {
    setSearchTerm(event.target.value); // Update search term state
  };

  const handleButtonClick = () => {
    // Handle submit button click logic here
    console.log('Submit button clicked');
  };

  return (
    <div className='containerStyle' style={{ alignItems: 'center', backgroundColor: 'white' }}>
      {/* Navbar header */}
      <div style={{ color: 'black', textAlign: 'left', backgroundColor: '#5DADE2', height: '10vh' }}>
        <h1>Attendance</h1> {/* Main heading */}
        <a className="nav-link" href="" style={{ position: 'absolute', left: 0, top: 0 }}>
          <FontAwesomeIcon icon={faHome} style={{ background: 'none', height: '26px', marginTop: '35px', marginLeft: '1450px' }} />
        </a>
      </div>
      {/* Sub navigation links */}
      <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '20px' }}>
        <h2 style={{ fontSize: '10px' }}>Home</h2>
        <h2 style={{ fontSize: '10px' }}>Attendance</h2>
      </div>

      {/* Displaying the Attendance Date and Class/Section information with Search Box */}
      <div style={{ textAlign: 'left', marginTop: '20px', display: 'flex', alignItems: 'center' }}>
        <h3 style={{ display: 'inline-block' }}>Attendance Date:</h3>
        <h3 style={{ display: 'inline-block', marginLeft: '200px' }}>Class: 9 / Section: 'A'</h3>
        {/* Search box */}
        <input
          type="text"
          placeholder="Search..."
          value={searchTerm}
          onChange={handleSearchTermChange}
          style={{ marginLeft: '700px', padding: '4px', display: 'inline-block' }}
        />
      </div>

      <hr style={{ width: '100%', height: '2px', backgroundColor: 'black', margin: '10px 0' }} />

      {/* MatrixTable aligned to the left */}
      <div style={{ marginLeft: '10px' }}>
        <MatrixTable attendanceData={attendanceData} searchTerm={searchTerm} />
      </div>

      {/* Submit button */}
      <div style={{ textAlign: 'center', marginTop: '250px' }}>
        <button onClick={handleButtonClick}>Submit</button>
      </div>
    </div>
  );
};

export default Navbar;
