import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome } from '@fortawesome/free-solid-svg-icons';

const MatrixTable = () => {
  const attendanceData = [
    { sno: 1, rollNo: '001', studentName: 'John Doe', class: '9', section: 'A', present: 'Yes', absent: 'No', leave: 'No' },
    { sno: 2, rollNo: '002', studentName: 'Jane Smith', class: '9', section: 'A', present: 'No', absent: 'Yes', leave: 'No' },
    { sno: 3, rollNo: '003', studentName: 'Michael Johnson', class: '9', section: 'A', present: 'No', absent: 'No', leave: 'Yes' }
  ];

  return (
    <div style={{ width: '50%', marginLeft: 'auto' }}>
      <table border="1" style={{ width: '100%' }}>
        <thead>
          <tr>
            <th>S.No</th>
            <th>Roll No</th>
            <th>Student Name</th>
            <th>Class</th>
            <th>Section</th>
            <th>Present</th>
            <th>Absent</th>
            <th>Leave</th>
          </tr>
        </thead>
        <tbody>
          {attendanceData.map((record, idx) => (
            <tr key={idx}>
              <td>{record.sno}</td>
              <td>{record.rollNo}</td>
              <td>{record.studentName}</td>
              <td>{record.class}</td>
              <td>{record.section}</td>
              <td>{record.present}</td>
              <td>{record.absent}</td>
              <td>{record.leave}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const Navbar = () => {
  const [selectedClass, setSelectedClass] = useState('');
  const [selectedSection, setSelectedSection] = useState('');
  const [selectedDate, setSelectedDate] = useState('');
  const [searchTerm, setSearchTerm] = useState('');

  const handleClassChange = (event) => {
    setSelectedClass(event.target.value);
  };

  const handleSectionChange = (event) => {
    setSelectedSection(event.target.value);
  };

  const handleDateChange = (event) => {
    setSelectedDate(event.target.value);
  };

  const handleSearchTermChange = (event) => {
    setSearchTerm(event.target.value);
  };

  return (
    <div className='containerStyle' style={{ alignItems: 'center', backgroundColor: 'white' }}>
      <div style={{ color: 'black', textAlign: 'left', backgroundColor: '#5DADE2', height: '10vh' }}>
        <h1>Student </h1>
        <a className="nav-link" href="" style={{ position: 'absolute', left: 0, top: 0 }}>
          <FontAwesomeIcon icon={faHome} style={{ background: 'none', height: '26px', marginTop: '35px', marginLeft: '1450px' }} />
        </a>
      </div>
      <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '20px' }}>
        <h2 style={{ fontSize: '10px' }}>Home</h2>
        <h2 style={{ fontSize: '10px' }}>Attendance</h2>
      </div>
      <hr style={{ width: '100%', height: '2px', backgroundColor: 'black', margin: '10px 0' }} />

      <div style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
        <div className="fill-attendance-container" style={{ marginRight: '10px' }}>
          <div className="fill-attendance-box">
            <h2 className="fill-attendance-title">Fill Attendance</h2>
            <div className="fill-attendance-details">
              <div className="dropdown-container" style={{ marginBottom: '200px' }}>
                <label htmlFor="classDropdown" className="dropdown-label">Class:</label>
                <select
                  id="classDropdown"
                  value={selectedClass}
                  onChange={handleClassChange}
                  className="dropdown-select"
                >
                  <option value="">Select Class</option>
                  <option value="1">1</option>
                  <option value="2">2</option>
                </select>
              </div>

              <div className="dropdown-container" style={{ marginBottom: '25px' }}>
                <label htmlFor="sectionDropdown" className="dropdown-label">Section:</label>
                <select
                  id="sectionDropdown"
                  value={selectedSection}
                  onChange={handleSectionChange}
                  className="dropdown-select"
                >
                  <option value="">Select Section</option>
                  <option value="A">A</option>
                  <option value="B">B</option>
                </select>
              </div>

              <div className="dropdown-container" style={{ marginBottom: '20px' }}>
                <label htmlFor="datePicker" className="dropdown-label">Date:</label>
                <input
                  type="date"
                  id="datePicker"
                  value={selectedDate}
                  onChange={handleDateChange}
                  className="date-input"
                  style={{ width: '142px' }}
                />
              </div>

              <div className="buttons-container">
                <button className="fill-attendance-button" style={{ background: 'green', color: 'white',marginRight: '100px', }}>Fill Attendance</button>
              </div>
            </div>
          </div>
        </div>
        <MatrixTable />
      </div>

      <div className="view-attendance-container">
        <div className="view-attendance-box">
          <div className="buttons-container">
            <button className="view-attendance-button" style={{ background: 'blue', color: 'white', }}>View Attendance</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Navbar;
