import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome } from '@fortawesome/free-solid-svg-icons';

// MatrixTable component definition
const MatrixTable = () => {
  // Sample data representing class names and corresponding payment particulars
  const classData = [
    {
      className: 'Class 1',
      paymentParticulars: [
        { name: 'No of Payments', amount: 2500 },
        { name: 'Payment from Date', amount: 6000 },
        { name: 'Payment To Date', amount: 1200 },
        { name: 'Late Fee % Per Day', amount: 1200 },
        { name: 'Book Fee', amount: 1200 },
        { name: 'Management Fee', amount: 1200 },
        { name: 'Security Deposit', amount: 1200 },
        { name: 'Stationary Fee', amount: 1200 },
        { name: 'Transportation Fee', amount: 1200 },
        { name: 'Taxes', amount: 1200 },
        { name: 'Total', amount: 1200 }
      ]
    }
  ];

  // Logic to render the table using the classData array

  return (
    <div style={{ width: '50%', marginLeft: 'auto' }}>
      <table border="1" style={{ width: '100%' }}>
        <thead>
          <tr>
            <th>Class Name</th>
            {classData[0].paymentParticulars.map((particular, index) => (
              <th key={index}>{particular.name}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {classData.map((classItem, idx) => (
            <tr key={idx}>
              <td>{classItem.className}</td>
              {classItem.paymentParticulars.map((particular, index) => (
                <td key={index}>{particular.amount}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const Attendance = () => {
  const [selectedMedium, setSelectedMedium] = useState('Hindi');
  const [selectedSession, setSelectedSession] = useState('2013-14');
  const [selectedClass, setSelectedClass] = useState('1');
  const [selectedSection, setSelectedSection] = useState(null);
  const [selectedDate, setSelectedDate] = useState(null);
  const [attendanceData, setAttendanceData] = useState([]);
  const [existingData, setExistingData] = useState([
    { id: 1, name: 'John Doe', class: '9', section: 'A', rollNo: '001' },
    { id: 2, name: 'Jane Smith', class: '9', section: 'A', rollNo: '002' },
    { id: 3, name: 'Michael Johnson', class: '9', section: 'A', rollNo: '003' },
  ]);
  const [adminClicked, setAdminClicked] = useState(false); // Track if Admin button is clicked
  const [sidebarOpen, setSidebarOpen] = useState(false); // Track sidebar open/close state

  const handleMediumChange = (e) => setSelectedMedium(e.target.value);
  const handleSessionChange = (e) => setSelectedSession(e.target.value);
  const handleClassChange = (e) => setSelectedClass(e.target.value);
  const handleSectionChange = (e) => setSelectedSection(e.target.value);
  const handleDateChange = (e) => setSelectedDate(e.target.value);

  const handleFillAttendance = () => {
    // Open sidebar
    setSidebarOpen(true);
  };

  const handleViewAttendance = () => {
    // Logic to view attendance data
    console.log('View Attendance clicked');
  };

  return (
    <div className="attendance-container">
      <div className="home-icon">
        <FontAwesomeIcon icon={faHome} />
      </div>
      <div className="medium-session-container">
        <div className="medium-session-title">
          <label htmlFor="mediumDropdown" className="header-label">Medium:</label>
          <select
            id="mediumDropdown"
            value={selectedMedium}
            onChange={handleMediumChange}
            className="dropdown-select"
          >
            <option value="Hindi">Hindi</option>
            <option value="English">English</option>
          </select>
        </div>
        <div className="medium-session-title">
          <label htmlFor="sessionDropdown" className="header-label">Session:</label>
          <select
            id="sessionDropdown"
            value={selectedSession}
            onChange={handleSessionChange}
            className="dropdown-select"
          >
            <option value="2012-13">2012-13</option>
            <option value="2013-14">2013-14</option>
          </select>
        </div>
      </div>
      <hr style={{ width: '100%', height: '2px', backgroundColor: 'black', margin: '10px 0' }} />

      {/* Fill Attendance section */}
      <div className="fill-attendance-container">
        <div className="fill-attendance-box">
          <h2 className="fill-attendance-title">Fill Attendance</h2>
          <div className="fill-attendance-details">
            {/* Class dropdown */}
            <div className="dropdown-container" style={{ marginBottom: '25px' }}>
              <label htmlFor="classDropdown" className="dropdown-label">Class:</label>
              <br /> {/* Line break for new row */}
              <select
                id="classDropdown"
                value={selectedClass}
                onChange={handleClassChange}
                className="dropdown-select"
              >
                <option value="">Select Class</option>
                <option value="1">1</option>
                <option value="2">2</option>
                {/* Add more class options as needed */}
              </select>
            </div>

            {/* Section dropdown */}
            <div className="dropdown-container" style={{ marginBottom: '25px' }}>
              <label htmlFor="sectionDropdown" className="dropdown-label">Section:</label>
              <br /> {/* Line break for new row */}
              <select
                id="sectionDropdown"
                value={selectedSection}
                onChange={handleSectionChange}
                className="dropdown-select"
              >
                <option value="">Select Section</option>
                <option value="A">A</option>
                <option value="B">B</option>
                {/* Add more section options as needed */}
              </select>
            </div>

            {/* Date picker */}
            <div className="dropdown-container" style={{ marginBottom: '20px' }}>
              <label htmlFor="datePicker" className="dropdown-label">Date:</label>
              <br /> {/* Line break for new row */}
              <input
                type="date"
                id="datePicker"
                value={selectedDate}
                onChange={handleDateChange}
                className="date-input"
                style={{ width: '142px' }} // Adjust the width as needed to match the dropdowns
              />
            </div>

            {/* Buttons */}
            <div className="buttons-container">
              <button className="fill-attendance-button" onClick={handleFillAttendance}>
                Fill Attendance
              </button>
              <button className="view-attendance-button" onClick={handleViewAttendance}>
                View Attendance
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Render the MatrixTable component */}
      <MatrixTable />
    </div>
  );
};

export default Attendance;
