import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome } from '@fortawesome/free-solid-svg-icons'; // Required icons
import './Attendance.css'; // Import the CSS file for styling
import { Link } from 'react-router-dom';

const Attendance = () => {
  const [selectedMedium, setSelectedMedium] = useState('Hindi'); // State for selected medium
  const [selectedSession, setSelectedSession] = useState('2013-14'); // State for selected session
  const [roleDetails, setRoleDetails] = useState(null); // State for role details
  const [selectedRole, setSelectedRole] = useState(null); // State for selected role
  const [selectedClass, setSelectedClass] = useState('1'); // State for selected class
  const [selectedSection, setSelectedSection] = useState(null); // State for selected section
  const [selectedDate, setSelectedDate] = useState(null); // State for selected date
  const [attendanceData, setAttendanceData] = useState([]); // State for attendance data
  const [existingData, setExistingData] = useState([
    { id: 1, name: 'John Doe', class: '9', section: 'A', rollNo: '001' },
    { id: 2, name: 'Jane Smith', class: '9', section: 'A', rollNo: '002' },
    { id: 3, name: 'Michael Johnson', class: '9', section: 'A', rollNo: '003' },
  ]);
  const [currentView, setCurrentView] = useState('Admin'); // Default view is Admin

  // Function to handle medium change
  const handleMediumChange = (e) => setSelectedMedium(e.target.value);

  // Function to handle session change
  const handleSessionChange = (e) => setSelectedSession(e.target.value);

  // Function to handle role click event
  const handleRoleClick = (role) => {
    if (role === 'Admin') {
      setRoleDetails({ name: 'Admin', description: 'Admin details here' });
      setSelectedRole('Admin');
      setCurrentView('Admin'); // Set current view to Admin
    } else if (role === 'Staff') {
      setRoleDetails({ name: 'Staff', description: 'Staff details here' });
      setSelectedRole('Staff');
    } else if (role === 'Student') {
      setRoleDetails({ name: 'Student', description: 'Student details here' });
      setSelectedRole('Student');
    }
  };

  // Function to handle section click
  const handleSectionClick = (section) => setSelectedSection(section);

  // Function to handle date change
  const handleDateChange = (e) => setSelectedDate(e.target.value);

  // Function to fetch attendance data
  const handleFetchAttendance = () => {
    // Dummy data used for demonstration
    const dummyData = [
      { id: 4, name: 'Emily Watson', class: '9', section: 'B', rollNo: '004', present: 20, absent: 2, leave: 3 },
      { id: 5, name: 'Daniel Brown', class: '9', section: 'B', rollNo: '005', present: 21, absent: 1, leave: 1 },
    ];

    // Merge existing data with fetched attendance data based on unique identifiers (e.g., id, rollNo)
    const mergedData = [...existingData];
    dummyData.forEach((newData) => {
      const existingIndex = mergedData.findIndex((data) => data.rollNo === newData.rollNo);
      if (existingIndex !== -1) {
        mergedData[existingIndex] = { ...mergedData[existingIndex], ...newData };
      } else {
        mergedData.push(newData);
      }
    });
    setExistingData(mergedData);
    setAttendanceData(dummyData);
  };

  // Function to handle home icon click
  const handleHomeIconClick = () => {
    setCurrentView('Admin'); // Set current view to Admin when home icon is clicked
  };

  return (
    <div className="attendance-container">
      {/* Home Icon */}
      {currentView !== 'Admin' && (
        <div className="home-icon" onClick={handleHomeIconClick}>
          <FontAwesomeIcon icon={faHome} style={{ marginRight: '10px', fontSize: '24px' }} />
        </div>
      )}

      {/* Medium and Session Dropdowns */}
      <div className="medium-session-container">
        <div className="medium-session-title">
          <label htmlFor="mediumDropdown" className="header-label">Medium:</label>
          <select
            id="mediumDropdown"
            value={selectedMedium}
            onChange={handleMediumChange}
            className="dropdown-select"
          >
            <option value="Hindi">Hindi</option>
            <option value="English">English</option>
          </select>
        </div>
        <div className="medium-session-title">
          <label htmlFor="sessionDropdown" className="header-label">Session:</label>
          <select
            id="sessionDropdown"
            value={selectedSession}
            onChange={handleSessionChange}
            className="dropdown-select"
          >
            <option value="2012-13">2012-13</option>
            <option value="2013-14">2013-14</option>
          </select>
        </div>
      </div>

      {/* Attendance Header */}
      <div className="attendance-header">
        <h1 className="attendance-title">Attendance Management</h1>
      </div>

      {/* Main Panel */}
      <div className="main-panel">
        {/* Buttons */}
        {/* <button
          className="main-button"
          style={{ width: '250px', height: '150px', marginRight: '100px' }}
          onClick={() => handleRoleClick('Admin')}
        >
          <FontAwesomeIcon icon={faUser} style={{ marginRight: '200px', fontSize: '60px' }} />
          Admin
        </button>
        <button
          className="main-button"
          style={{ width: '250px', height: '150px', marginRight: '100px' }}
          onClick={() => handleRoleClick('Staff')}
        >
          <FontAwesomeIcon icon={faUsers} style={{ marginRight: '200px', fontSize: '60px' }} />
          Staff
        </button>
        <button
          className="main-button"
          style={{ width: '250px', height: '150px' }}
          onClick={() => handleRoleClick('Student')}
        >
          <FontAwesomeIcon icon={faUserGraduate} style={{ marginRight: '200px', fontSize: '60px' }} />
          Student
        </button> */}
      </div>
    </div>
  );
};

export default Attendance;
