import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome } from '@fortawesome/free-solid-svg-icons';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS

// Sample attendance data
const initialAttendanceData = [
  { sno: 1, rollNo: '001', studentName: 'John Doe', class: '9', section: 'A', present: 1, absent: 0, leave: 0, month: 1 },
  { sno: 2, rollNo: '002', studentName: 'Jane Smith', class: '9', section: 'A', present: 0, absent: 1, leave: 0, month: 2 },
  { sno: 3, rollNo: '003', studentName: 'Michael Johnson', class: '9', section: 'A', present: 0, absent: 0, leave: 1, month: 3 }
];

// Function to get total days in a month
const getTotalDaysInMonth = (year, month) => {
  return new Date(year, month, 0).getDate();
};

// Function to calculate attendance percentage
const calculateAttendancePercentage = (totalDays, presentDays) => {
  if (totalDays === 0) return 0; // To handle division by zero
  return ((presentDays / totalDays) * 100).toFixed(2);
};

// MatrixTable component to display attendance data in a table
const MatrixTable = ({ data }) => {
  return (
    <div className="table-responsive">
      <table className="table table-bordered table-striped">
        {/* Table header */}
        <thead className="bg-primary text-white">
          <tr>
            <th>S.No</th>
            <th>Roll No</th>
            <th>Student Name</th>
            <th className="font-weight-bold">Class</th> {/* Bold "Class" title */}
            <th className="font-weight-bold">Section</th> {/* Bold "Section" title */}
            <th>Total Days</th>
            <th>Present</th>
            <th>Absent</th>
            <th>Leave</th>
            <th>Attendance %</th> {/* New column for attendance percentage */}
          </tr>
        </thead>
        {/* Table body */}
        <tbody>
          {/* Mapping through attendance data to display rows */}
          {data.map((record, idx) => (
            <tr key={idx}>
              <td>{record.sno}</td>
              <td>{record.rollNo}</td>
              <td>{record.studentName}</td>
              <td>{record.class}</td>
              <td>{record.section}</td>
              <td>{getTotalDaysInMonth(new Date().getFullYear(), record.month)}</td>
              <td>{record.present}</td>
              <td>{record.absent}</td>
              <td>{record.leave}</td>
              {/* Calculate and display attendance percentage */}
              <td>{calculateAttendancePercentage(getTotalDaysInMonth(new Date().getFullYear(), record.month), record.present)}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

// Navbar component
const Navbar = () => {
  // State variables
  const [selectedClass, setSelectedClass] = useState(''); // Selected class
  const [selectedSection, setSelectedSection] = useState(''); // Selected section
  const [selectedMonth, setSelectedMonth] = useState(''); // Selected month
  const [searchTerm, setSearchTerm] = useState(''); // Search term
  const [filteredData, setFilteredData] = useState(initialAttendanceData); // Filtered data state
  const [attendanceType, setAttendanceType] = useState('self'); // Attendance type (self or class)

  // Event handlers for dropdown and input changes
  const handleClassChange = (event) => {
    setSelectedClass(event.target.value); // Update selected class state
    filterData(event.target.value, selectedSection, selectedMonth); // Filter data based on class change
  };

  const handleSectionChange = (event) => {
    setSelectedSection(event.target.value); // Update selected section state
    filterData(selectedClass, event.target.value, selectedMonth); // Filter data based on section change
  };

  const handleMonthChange = (event) => {
    setSelectedMonth(event.target.value); // Update selected month state
    filterData(selectedClass, selectedSection, event.target.value); // Filter data based on month change
  };

  const handleAttendanceTypeChange = (event) => {
    setAttendanceType(event.target.value); // Update attendance type (self or class)
  };

  const handleSearch = (event) => {
    setSearchTerm(event.target.value); // Update search term
    filterData(selectedClass, selectedSection, selectedMonth, event.target.value); // Filter data based on search term
  };

  const handleViewAttendance = () => {
    console.log('View Attendance clicked');
    // Add your logic to handle view attendance action
  };

  const handleFillAttendance = () => {
    console.log('Fill Attendance clicked');
    // Add your logic to handle fill attendance action
  };

  const filterData = (classValue, sectionValue, monthValue, searchTermValue) => {
    let filtered = initialAttendanceData;
    if (classValue) {
      filtered = filtered.filter(record => record.class === classValue);
    }
    if (sectionValue) {
      filtered = filtered.filter(record => record.section === sectionValue);
    }
    if (monthValue) {
      filtered = filtered.filter(record => record.month === parseInt(monthValue));
    }
    if (searchTermValue) {
      filtered = filtered.filter(record => record.rollNo.includes(searchTermValue)); // Search by roll number
    }
    setFilteredData(filtered);
  };

  return (
    <div className='container-fluid'>
      {/* Navbar header */}
      <div className='bg-primary text-white py-3'>
        <h1 className='pl-3'>Staff </h1> {/* Main heading */}
        <a className="nav-link" href="/">
          <FontAwesomeIcon icon={faHome} className="mr-3" />
        </a>
      </div>
      {/* Dropdown for Attendance Type */}
      <div className='d-flex align-items-center mb-3'>
        {/* Attendance header */}
        <h2 className='mr-3' style={{ fontSize: '10px' }}>Attendance</h2>
        <select
          value={attendanceType}
          onChange={handleAttendanceTypeChange}
          className='form-control w-25'
        >
          <option value="self">Self</option>
          <option value="class">Class</option>
        </select>
      </div>
      <hr className='my-3' />

      {/* Attendance filling and viewing section */}
      <div className='d-flex flex-column'>
        {/* Dropdowns in a single row with gaps */}
        {attendanceType === 'class' && (
          <div className='d-flex align-items-center mb-3'>
            <div className='mr-3'>
              <label htmlFor="classDropdown"><strong>Class:</strong></label> {/* Bold "Class" title */}
              <select
                id="classDropdown"
                value={selectedClass}
                onChange={handleClassChange}
                className='form-control'
              >
                <option value="">Select Class</option>
                <option value="1">1</option>
                <option value="2">2</option>
              </select>
            </div>
            <div className='mr-3'>
              <label htmlFor="sectionDropdown"><strong>Section:</strong></label> {/* Bold "Section" title */}
              <select
                id="sectionDropdown"
                value={selectedSection}
                onChange={handleSectionChange}
                className='form-control'
              >
                <option value="">Select Section</option>
                <option value="A">A</option>
                <option value="B">B</option>
                {/* Add other section options */}
              </select>
            </div>
            <div className='mr-3'>
              <label htmlFor="monthDropdown"><strong>Month:</strong></label> {/* Bold "Month" title */}
              <select
                id="monthDropdown"
                value={selectedMonth}
                onChange={handleMonthChange}
                className='form-control'
              >
                <option value="">Select Month</option>
                <option value="1">January</option>
                <option value="2">February</option>
                <option value="3">March</option>
                <option value="4">April</option>
                <option value="5">May</option>
                <option value="6">June</option>
                <option value="7">July</option>
                <option value="8">August</option>
                <option value="9">September</option>
                <option value="10">October</option>
                <option value="11">November</option>
                <option value="12">December</option>
              </select>
            </div>
            <input
              type="text"
              placeholder="Search Roll Number"
              value={searchTerm}
              onChange={handleSearch}
              className='form-control mr-3'
            />
            <button className="btn btn-danger mr-1" onClick={handleViewAttendance}>View Attendance</button>
            <button className="btn btn-success" onClick={handleFillAttendance}>Fill Attendance</button>
          </div>
        )}
        <MatrixTable data={filteredData} />
      </div>
    </div>
  );
};

export default Navbar;
