// export default Attendance;

import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome, faDollarSign, faUserGraduate, faUserCheck } from '@fortawesome/free-solid-svg-icons'; // Required icons
import './Attendance.css'; // Import the CSS file for styling

const Attendance = () => {
  // Function to handle home icon click
  const handleHomeIconClick = () => {
    // Handle any logic you need for the home icon click
  };

  return (
    <div className="attendance-container">
      {/* Home Icon */}
      <div className="home-icon" onClick={handleHomeIconClick}>
        <FontAwesomeIcon icon={faHome} style={{ marginRight: '10px', fontSize: '24px' }} />
      </div>

      {/* Attendance Header */}
      <div className="attendance-header">
        <h1 className="attendance-title">Attendance Management</h1>
      </div>

      {/* Admin Box */}
      <div
        className='Data-Exploration'
        style={{
          color: 'black',
          height: 'auto',
          width: '50vmin',
          padding: '10px',
          fontSize: '20px',
          borderBottom: '1px solid #02133805',
          boxShadow: '2px 2px 5px 2px rgba(0, 0, 0, 0.1)',
          borderRadius: '10px',
          background: 'linear-gradient(to right, #F4D03F, #E6E6FA)',
          position: 'relative',
          overflow: 'hidden',
          transition: 'background-color 0.3s ease',
          marginLeft: '50px',
          cursor: 'pointer',
        }}
      >
        <FontAwesomeIcon icon={faDollarSign} style={{ color: '#B7950B', height: '56px' }} />
        <h3>Admin</h3>
      </div>

      {/* Staff Box */}
      <div
        className='Data-Exploration'
        style={{
          color: 'black',
          height: 'auto',
          width: '50vmin',
          padding: '10px',
          fontSize: '20px',
          borderBottom: '1px solid #02133805',
          boxShadow: '2px 2px 5px 2px rgba(0, 0, 0, 0.1)',
          borderRadius: '10px',
          background: 'linear-gradient(to right, #A3E4D7, #E6E6FA)',
          position: 'relative',
          overflow: 'hidden',
          transition: 'background-color 0.3s ease',
          marginLeft: '540px',
          
          cursor: 'pointer',
        }}
      >
        <FontAwesomeIcon icon={faUserCheck} style={{ color: '#2874A6', height: '56px' }} />
        <h3>Staff</h3>
      </div>

      {/* Student Box */}
      <div
        className='Data-Exploration'
        style={{
          color: 'black',
          height: 'auto',
          width: '50vmin',
          padding: '10px',
          fontSize: '20px',
          borderBottom: '1px solid #02133805',
          boxShadow: '2px 2px 5px 2px rgba(0, 0, 0, 0.1)',
          borderRadius: '10px',
          background: 'linear-gradient(to right, #A3E4D7, #E6E6FA)',
          position: 'relative',
          overflow: 'hidden',
          transition: 'background-color 0.3s ease',
          marginLeft: '1000px',
          cursor: 'pointer',
        }}
      >
        <FontAwesomeIcon icon={faUserGraduate} style={{ color: '#5DADE2', height: '56px' }} />
        <h3>Student</h3>
      </div>
    </div>
  );
};

export default Attendance;
