// export default Navbar;
import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome } from '@fortawesome/free-solid-svg-icons';

const Navbar = () => {
  const [selectedClass, setSelectedClass] = useState('');
  const [selectedSection, setSelectedSection] = useState('');
  const [selectedDate, setSelectedDate] = useState('');
  const [searchTerm, setSearchTerm] = useState('');

  const handleClassChange = (event) => {
    setSelectedClass(event.target.value);
  };

  const handleSectionChange = (event) => {
    setSelectedSection(event.target.value);
  };

  const handleDateChange = (event) => {
    setSelectedDate(event.target.value);
  };

  const handleSearchTermChange = (event) => {
    setSearchTerm(event.target.value);
  };

  return (
    <div className='containerStyle' style={{ alignItems: 'center', backgroundColor: 'white' }}>
      <div style={{ color: 'black', textAlign: 'left', backgroundColor: '#5DADE2 ', height: '10vh' }}>
        <h1>Attendance Management</h1>
        <a className="nav-link" href="" style={{ position: 'absolute', left: 0, top: 0 }}>
          <FontAwesomeIcon icon={faHome} style={{ background: 'none', height: '26px', marginTop: '35px', marginLeft: '1450px' }} />
        </a>
      </div>
      <div style={{ display: 'flex', gap: '80px' }}>
        <h2>Home</h2>
        <h2>Attendance Management</h2>
      </div>

      {/* Fill Attendance section */}
      <div className="fill-attendance-container">
        <div className="fill-attendance-box">
          <h2 className="fill-attendance-title">Fill Attendance</h2>
          <div className="fill-attendance-details">
            <div className="dropdown-container" style={{ marginBottom: '25px' }}>
              <label htmlFor="classDropdown" className="dropdown-label">Class:</label>
              <select
                id="classDropdown"
                value={selectedClass}
                onChange={handleClassChange}
                className="dropdown-select"
              >
                <option value="">Select Class</option>
                <option value="1">1</option>
                <option value="2">2</option>
              </select>
            </div>

            <div className="dropdown-container" style={{ marginBottom: '25px' }}>
              <label htmlFor="sectionDropdown" className="dropdown-label">Section:</label>
              <select
                id="sectionDropdown"
                value={selectedSection}
                onChange={handleSectionChange}
                className="dropdown-select"
              >
                <option value="">Select Section</option>
                <option value="A">A</option>
                <option value="B">B</option>
              </select>
            </div>

            <div className="dropdown-container" style={{ marginBottom: '20px' }}>
              <label htmlFor="datePicker" className="dropdown-label">Date:</label>
              <input
                type="date"
                id="datePicker"
                value={selectedDate}
                onChange={handleDateChange}
                className="date-input"
                style={{ width: '142px' }}
              />
            </div>

            <div className="buttons-container">
              <button className="fill-attendance-button">Fill Attendance</button>
            </div>
          </div>
        </div>
      </div>

      <hr style={{ width: '100%', height: '2px', backgroundColor: 'black', margin: '20px 0' }} />

      {/* Search box */}
      <div style={{ display: 'flex', justifyContent: 'flex-end', alignItems: 'center', marginBottom: '20px' }}>
        <label htmlFor="search" style={{ marginRight: '10px' }}>Search:</label>
        <input
          type="text"
          id="search"
          value={searchTerm}
          onChange={handleSearchTermChange}
          placeholder="Search..."
        />
      </div>
    </div>
  );
};

export default Navbar;
