import React, { useState } from 'react';
import './Header.css'; // Styling CSS file
 
const Header = ({ title }) => {
  const [mediumOptions, setMediumOptions] = useState(['English', 'Hindi']);
  const [sessionOptions, setSessionOptions] = useState(['2024', '2025']);
  const [typingOptions, setTypingOptions] = useState(['English', 'Hindi']);
  const [classList, setClassList] = useState(['All', 1, 2, 3]); // Adding 'All' as the first item in the array
  const [selectedClass, setSelectedClass] = useState('');
 
  return (
    <div>
      <div className="header-container">
        <nav className="right-navbar">
          <div className="filter-options">
            <div className="filter-dropdown">
              <label htmlFor="medium">Medium:</label>
              <select
                id="medium"
                value={mediumOptions}
                onChange={(e) => setMediumOptions(e.target.value)}
                className="filter-select"
              >
                {mediumOptions.map((option, index) => (
                  <option key={index} value={option}>{option}</option>
                ))}
              </select>
            </div>
            <div className="filter-dropdown">
              <label htmlFor="session">Session:</label>
              <select
                id="session"
                value={sessionOptions}
                onChange={(e) => setSessionOptions(e.target.value)}
                className="filter-select"
              >
                {sessionOptions.map((option, index) => (
                  <option key={index} value={option}>{option}</option>
                ))}
              </select>
            </div>
            <div className="filter-dropdown">
              <label htmlFor="typing">Typing:</label>
              <select
                id="typing"
                value={typingOptions}
                onChange={(e) => setTypingOptions(e.target.value)}
                className="filter-select"
              >
                {typingOptions.map((option, index) => (
                  <option key={index} value={option}>{option}</option>
                ))}
              </select>
            </div>
          </div>
        </nav>
      </div>
      <nav className="student-management-navbar">
        <div className="student-management-title">Student Management</div>
        <div className="student-management-links">
          <div className="student-management-link">Home</div>
          <div className="student-management-link">Student</div>
          <div className="student-management-link">Student Registration List</div>
        </div>
      </nav>
      <hr style={{ width: '100%', height: '2px', backgroundColor: '#3498DB', margin: '10px 0' }} />
      <nav className="Registration-List-navbar">
        <div className="Registration-List-box">
          <div className="Registration-List-title">Registration List</div>
          <hr style={{ width: '100%', height: '0px', backgroundColor: 'black', margin: '10px 0' }} />
          <div className="class-dropdown">
            <label htmlFor="class" className="bold-text">Class:</label>
            <select
              id="class"
              className="filter-select"
              value={selectedClass}
              onChange={(e) => setSelectedClass(e.target.value)}
            >
              {classList.map((classNumber, index) => (
                <option key={index} value={classNumber}>{classNumber}</option>
              ))}
            </select>
          </div>
        </div>
      </nav>
      <hr style={{ width: '100%', height: '1px', backgroundColor: '#BFC9CA', margin: '10px 0' }} />
      <nav className="search-navbar">
        <input
          type="text"
          placeholder="Search..."
          className="filter-input"
        />
      </nav>
<hr style={{ width: '100%', height: '1px', backgroundColor: 'black', margin: '10px 0' }} />
<div style={{ display: 'flex', gap: '70px' }}>
<h>Student ID</h>
<h>Student Name</h>
<h>Father's Name</h>
<h>Class</h>
<h>Contact No</h>
<h>Registration Date</h>
<h>Remark</h>
<h>Make Admission </h>
<h>Pirnt</h>
<h>Delete</h>
      </div>
 
<hr style={{ width: '100%', height: '1px', backgroundColor: 'black', margin: '10px 0' }} />
      </div>
 
  );
};
 
export default Header;
