// export default Navbar;
import React from 'react';

// MatrixTable component definition
const MatrixTable = () => {
  // Sample data representing attendance records
  const attendanceData = [
    { sno: 1, rollNo: '001', studentName: 'John Doe', class: '9', section: 'A', present: 'Yes', absent: 'No', leave: 'No' },
    { sno: 2, rollNo: '002', studentName: 'Jane Smith', class: '9', section: 'A', present: 'No', absent: 'Yes', leave: 'No' },
    { sno: 3, rollNo: '003', studentName: 'Michael Johnson', class: '9', section: 'A', present: 'No', absent: 'No', leave: 'Yes' }
  ];

  return (
    <div style={{ width: '50%', marginLeft: 'auto' }}>
      <table border="1" style={{ width: '100%' }}>
        <thead>
          <tr>
            <th>S.No</th>
            <th>Roll No</th>
            <th>Student Name</th>
            <th>Class</th>
            <th>Section</th>
            <th>Present</th>
            <th>Absent</th>
            <th>Leave</th>
          </tr>
        </thead>
        <tbody>
          {attendanceData.map((record, idx) => (
            <tr key={idx}>
              <td>{record.sno}</td>
              <td>{record.rollNo}</td>
              <td>{record.studentName}</td>
              <td>{record.class}</td>
              <td>{record.section}</td>
              <td>{record.present}</td>
              <td>{record.absent}</td>
              <td>{record.leave}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default MatrixTable;
