import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome } from '@fortawesome/free-solid-svg-icons';
import './Attendance.css'; // Import the CSS file for styling
import { Link } from 'react-router-dom';

const Attendance = () => {
  const [selectedMedium, setSelectedMedium] = useState('Hindi');
  const [selectedSession, setSelectedSession] = useState('2013-14');
  const [selectedClass, setSelectedClass] = useState('1');
  const [selectedSection, setSelectedSection] = useState(null);
  const [selectedDate, setSelectedDate] = useState(null);
  const [attendanceData, setAttendanceData] = useState([]);
  const [existingData, setExistingData] = useState([
    { id: 1, name: 'John Doe', class: '9', section: 'A', rollNo: '001' },
    { id: 2, name: 'Jane Smith', class: '9', section: 'A', rollNo: '002' },
    { id: 3, name: 'Michael Johnson', class: '9', section: 'A', rollNo: '003' },
  ]);
  const [adminClicked, setAdminClicked] = useState(false); // Track if Admin button is clicked
  const [sidebarOpen, setSidebarOpen] = useState(false); // Track sidebar open/close state

  const handleMediumChange = (e) => setSelectedMedium(e.target.value);
  const handleSessionChange = (e) => setSelectedSession(e.target.value);
  const handleClassChange = (e) => setSelectedClass(e.target.value);
  const handleSectionChange = (e) => setSelectedSection(e.target.value);
  const handleDateChange = (e) => setSelectedDate(e.target.value);

  const handleFillAttendance = () => {
    // Open sidebar
    setSidebarOpen(true);
  };

  return (
    <div className="attendance-container">
      <div className="home-icon">
        <FontAwesomeIcon icon={faHome} />
      </div>
      <div className="medium-session-container">
        <div className="medium-session-title">
          <label htmlFor="mediumDropdown" className="header-label">Medium:</label>
          <select
            id="mediumDropdown"
            value={selectedMedium}
            onChange={handleMediumChange}
            className="dropdown-select"
          >
            <option value="Hindi">Hindi</option>
            <option value="English">English</option>
          </select>
        </div>
        <div className="medium-session-title">
          <label htmlFor="sessionDropdown" className="header-label">Session:</label>
          <select
            id="sessionDropdown"
            value={selectedSession}
            onChange={handleSessionChange}
            className="dropdown-select"
          >
            <option value="2012-13">2012-13</option>
            <option value="2013-14">2013-14</option>
          </select>
        </div>
      </div>
      <div className="attendance-header">
        <h1 className="attendance-management-title">Attendance Management</h1>
        <div className="fill-attendance-container">
          <div className="fill-attendance-box">
            <h2 className="fill-attendance-title">Fill Attendance</h2>
            <div className="fill-attendance-details">
              <div className="selection-container">
                <label htmlFor="classDropdown" className="class-label">Class:</label>
                <select
                  id="classDropdown"
                  value={selectedClass}
                  onChange={handleClassChange}
                  className="dropdown-select"
                >
                  <option value="1">1</option>
                  <option value="2">2</option>
                  {/* Add more class options as needed */}
                </select>
              </div>
              <div className="selection-container">
                <label htmlFor="sectionDropdown" className="section-label">Section:</label>
                <select
                  id="sectionDropdown"
                  value={selectedSection}
                  onChange={handleSectionChange}
                  className="dropdown-select"
                >
                  <option value="A">A</option>
                  <option value="B">B</option>
                  {/* Add more section options as needed */}
                </select>
              </div>
              <div className="selection-container">
                <label htmlFor="datePicker" className="date-label">Date:</label>
                <input
                  type="date"
                  id="datePicker"
                  value={selectedDate}
                  onChange={handleDateChange}
                  className="date-input"
                  style={{ width: '142px' }} // Adjust the width as needed to match the dropdowns
                />
              </div>
            </div>
            <div className="fill-attendance-buttons">
              <button className="fill-attendance-button">Fill Attendance</button>
            </div>
            <div className="view-attendance-container">
              <button className="view-attendance-button">View Attendance</button>
            </div>
          </div>
        </div>
      </div>
      <div className={`sidebar ${sidebarOpen ? 'open' : ''}`}>
        <h2 className="sidebar-title">Matrix</h2>
        <div className="matrix-table">
          <div className="matrix-row">
            <div className="matrix-cell">S.No</div>
            <div className="matrix-cell">Roll No</div>
          </div>
          {existingData.map((data, index) => (
            <div key={data.id} className="matrix-row">
              <div className="matrix-cell">{index + 1}</div>
              <div className="matrix-cell">{data.rollNo}</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Attendance;
