import React, { useState } from 'react';
import './Attendance.css'; // Import the CSS file for styling

const Attendance = () => {
  const [selectedMedium, setSelectedMedium] = useState('Hindi'); // Default value for Medium
  const [selectedSession, setSelectedSession] = useState('2013-14'); // Default value for Session
  const [roleDetails, setRoleDetails] = useState(null); // State to store role details
  const [selectedRole, setSelectedRole] = useState(null); // State to track the selected role

  const handleMediumChange = (e) => {
    setSelectedMedium(e.target.value);
  };

  const handleSessionChange = (e) => {
    setSelectedSession(e.target.value);
  };

  const handleRoleClick = (role) => {
    // Logic to fetch and set role details based on the role clicked
    if (role === 'Admin') {
      setRoleDetails({ name: 'Admin', description: 'Admin details here' });
      setSelectedRole('Admin'); // Set selected role to Admin
    } else if (role === 'Teacher') {
      setRoleDetails({ name: 'Teacher', description: 'Teacher details here' });
      setSelectedRole('Teacher'); // Set selected role to Teacher
    } else if (role === 'Student') {
      setRoleDetails({ name: 'Student', description: 'Student details here' });
      setSelectedRole('Student'); // Set selected role to Student
    }
  };

  return (
    <div className="attendance-container">
      <div className="medium-session-container">
        <div className="medium-session-title">
          <label htmlFor="mediumDropdown" className="header-label">Medium:</label>
          <select
            id="mediumDropdown"
            value={selectedMedium}
            onChange={handleMediumChange}
            className="dropdown-select"
          >
            <option value="Hindi">Hindi</option>
            <option value="English">English</option>
          </select>
        </div>
        <div className="medium-session-title">
          <label htmlFor="sessionDropdown" className="header-label">Session:</label>
          <select
            id="sessionDropdown"
            value={selectedSession}
            onChange={handleSessionChange}
            className="dropdown-select"
          >
            <option value="2012-13">2012-13</option>
            <option value="2013-14">2013-14</option>
          </select>
        </div>
      </div>
      <div className="attendance-header">
        <h1 className="attendance-title">Attendance Management</h1>
      </div>
      <div className="main-panel">
        {selectedRole === null && ( // Show role buttons only if no role is selected
          <>
            <button className="main-button" style={{ width: '250px', height: '150px', marginRight: '100px' }} onClick={() => handleRoleClick('Admin')}>Admin</button>
            <button className="main-button" style={{ width: '250px', height: '150px', marginRight: '100px' }} onClick={() => handleRoleClick('Teacher')}>Teacher</button>
            <button className="main-button" style={{ width: '250px', height: '150px' }} onClick={() => handleRoleClick('Student')}>Student</button>
          </>
        )}
        {selectedRole === 'Admin' && (
          <button className="main-button" style={{ width: '250px', height: '150px' }} onClick={() => handleRoleClick(null)}>Back</button>
        )}
        {selectedRole === 'Teacher' && (
          <button className="main-button" style={{ width: '250px', height: '150px' }} onClick={() => handleRoleClick(null)}>Back</button>
        )}
        {selectedRole === 'Student' && (
          <button className="main-button" style={{ width: '250px', height: '150px' }} onClick={() => handleRoleClick(null)}>Back</button>
        )}
      </div>
      {roleDetails && (
        <div className="role-details">
          <h2>{roleDetails.name} Details</h2>
          <p>{roleDetails.description}</p>
        </div>
      )}
      {/* Other content related to attendance can go here */}
    </div>
  );
};

export default Attendance;
