// export default Attendance;
import React, { useState } from 'react';
import './Attendance.css'; // Import the CSS file for styling

const Attendance = () => {
  const [selectedMedium, setSelectedMedium] = useState('Hindi');
  const [selectedSession, setSelectedSession] = useState('2013-14');
  const [roleDetails, setRoleDetails] = useState(null);
  const [selectedRole, setSelectedRole] = useState(null);
  const [selectedClass, setSelectedClass] = useState('1');
  const [selectedSection, setSelectedSection] = useState(null);
  const [selectedDate, setSelectedDate] = useState(null);
  const [attendanceData, setAttendanceData] = useState([]);

  const handleMediumChange = (e) => setSelectedMedium(e.target.value);
  const handleSessionChange = (e) => setSelectedSession(e.target.value);
  const handleRoleClick = (role) => {
    if (role === 'Admin') {
      setRoleDetails({ name: 'Admin', description: 'Admin details here' });
      setSelectedRole('Admin');
    } else if (role === 'Teacher') {
      setRoleDetails({ name: 'Teacher', description: 'Teacher details here' });
      setSelectedRole('Teacher');
    } else if (role === 'Student') {
      setRoleDetails({ name: 'Student', description: 'Student details here' });
      setSelectedRole('Student');
    }
    setRoleDetails(null);
    setSelectedRole(role);
  };
  const handleSectionClick = (section) => setSelectedSection(section);
  const handleDateChange = (e) => setSelectedDate(e.target.value);

  const handleFetchAttendance = () => {
    // Here, you can fetch attendance data based on selectedClass, selectedSection, and selectedDate
    // For demonstration purposes, I'll set some dummy data
    const dummyData = [
      { sno: 1, rollNo: '001', studentName: 'John Doe', class: '9', section: 'A', month: 'January', present: 20, absent: 2, leave: 3 },
      { sno: 2, rollNo: '002', studentName: 'Jane Smith', class: '9', section: 'A', month: 'January', present: 21, absent: 1, leave: 1 },
      { sno: 3, rollNo: '003', studentName: 'Michael Johnson', class: '9', section: 'A', month: 'January', present: 22, absent: 0, leave: 0 },
    ];
    setAttendanceData(dummyData);
  };

  return (
    <div className="attendance-container">
      {/* Your existing code */}
      <div className="fill-attendance-title">
        {/* Your existing code */}
        <div className="fetch-attendance-button">
          <button className="main-button" onClick={handleFetchAttendance}>
            Fetch Attendance
          </button>
        </div>
        <div className="attendance-table">
          <h3>Attendance Information</h3>
          <table>
            <thead>
              <tr>
                <th>S.No</th>
                <th>Roll No.</th>
                <th>Student Name</th>
                <th>Class</th>
                <th>Section</th>
                <th>Month</th>
                <th>Present</th>
                <th>Absent</th>
                <th>Leave</th>
              </tr>
            </thead>
            <tbody>
              {attendanceData.map((data) => (
                <tr key={data.sno}>
                  <td>{data.sno}</td>
                  <td>{data.rollNo}</td>
                  <td>{data.studentName}</td>
                  <td>{data.class}</td>
                  <td>{data.section}</td>
                  <td>{data.month}</td>
                  <td>{data.present}</td>
                  <td>{data.absent}</td>
                  <td>{data.leave}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Attendance;
